css:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&family=Uncial+One&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

.certificate-container {
    width: 100%;
    min-height: 100vh;
    background: #FFFFFF;
    position: relative;
    padding: 3rem 4rem;
    font-family: 'Inter', sans-serif;
}

.certificate-decorations {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
    z-index: 1;
}

.decoration-top-left {
    position: absolute;
    top: 2rem;
    left: 2rem;
    width: 120px;
    height: 120px;
}

.decoration-top-right {
    position: absolute;
    top: 2rem;
    right: 2rem;
    width: 180px;
    height: 120px;
}

.decoration-bottom-left {
    position: absolute;
    bottom: 2rem;
    left: 2rem;
    width: 120px;
    height: 120px;
}

.decoration-bottom-right {
    position: absolute;
    bottom: 2rem;
    right: 2rem;
    width: 120px;
    height: 120px;
}

.certificate-content {
    position: relative;
    z-index: 2;
    text-align: center;
    max-width: 900px;
    margin: 0 auto;
}

.certificate-org-name {
    font-family: 'Uncial One', cursive;
    font-size: 3.5rem;
    font-weight: 900;
    color: #000000;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 1rem;
    text-shadow: 2px 2px 0px rgba(0, 0, 0, 0.1);
}

.certificate-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: #000000;
    margin-bottom: 3rem;
    letter-spacing: 0.05em;
}

.certificate-name {
    font-size: 3.5rem;
    font-weight: 900;
    color: #000000;
    margin: 2rem 0;
    letter-spacing: -0.02em;
    line-height: 1.2;
}

.certificate-description {
    font-size: 1rem;
    font-weight: 400;
    color: #666666;
    line-height: 1.6;
    max-width: 700px;
    margin: 2rem auto 4rem;
    text-align: center;
}

.certificate-signatures {
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    margin-top: 4rem;
    padding: 0 4rem;
}

.signature-block {
    text-align: center;
    flex: 1;
}

.signature-name {
    font-size: 1.1rem;
    font-weight: 700;
    color: #000000;
    margin-bottom: 0.5rem;
}

.signature-title {
    font-size: 0.9rem;
    font-weight: 400;
    color: #666666;
}

@media print {
    .certificate-container {
        padding: 2rem 3rem;
    }
    .certificate-org-name {
        font-size: 3rem;
    }
    .certificate-name {
        font-size: 3rem;
    }
}
